mapper.select:
SELECT A.* FROM (SELECT ROWNUM RR, R.* FROM (SELECT O.NO, O.PRODUCT_NO, P.NAME PNAME, O.QUANTITY, O.OR_DATE, O.STATE, O.ID, M.M_NAME MNAME, P.PRICE, P.STOCK FROM SHOP_ORDER O INNER JOIN SHOP_PRODUCT P ON O.PRODUCT_NO = P.NO INNER JOIN MEMBER M ON O.ID = M.MEM_ID ORDER BY O.NO) R) A
WHERE NO = #{no}
WHERE ID = #{id}

mapper.update:
UPDATE SHOP_ORDER
SET STATE = #{state}
WHERE NO  = #{no}
