mapper.insert:
INSERT INTO 
 MEMBER(MEM_ID,M_PASSWD, M_NAME,
        M_EMAIL, M_PHONE, ZIPCODE,
        ADDRESS, ADDRESS2, M_JOB)
VALUES(#{mem_id}
      ,#{m_passwd}
      ,#{m_name}
      ,#{m_email}
      ,#{m_phone}
      ,#{zipcode}
      ,#{address}
      ,#{address2}
      ,#{m_job}
       )

mapper.select:
SELECT MEM_ID, M_NAME, M_ROLL, M_PASSWD 
FROM MEMBER 
WHERE MEM_ID = #{mem_id} 
and m_passwd = #{m_passwd}

mapper.update:
   UPDATE  MEMBER SET 
	 M_PASSWD  = #{m_passwd}
	,M_EMAIL   = #{m_email }
	,M_PHONE   = #{m_phone }
	,ZIPCODE   = #{zipcode }
	,ADDRESS   = #{address }
	,ADDRESS2  = #{address2}
	,M_JOB     = #{m_job   }
	WHERE MEM_ID = #{mem_id}
